mapper.select:
select
g_id, c_id, g_name, g_price, g_isdiscount, g_describe, g_sales_volume, g_buy_person_time, g_stock,
g_storage_time, g_removal_time, g_status, es_1, es_2, es_3
from goods


        and g_id = #{gId}


        and c_id = #{cId}


        and g_name = #{gName}


        and g_price = #{gPrice}


        and g_isdiscount = #{gIsdiscount}


        and g_describe = #{gDescribe}


        and g_sales_volume = #{gSalesVolume}


        and g_buy_person_time = #{gBuyPersonTime}


        and g_stock = #{gStock}


        and g_storage_time = #{gStorageTime}


        and g_removal_time = #{gRemovalTime}


        and g_status = #{gStatus}


        and es_1 = #{es1}


        and es_2 = #{es2}


        and es_3 = #{es3}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from goods


        and g_id = #{gId}


        and c_id = #{cId}


        and g_name = #{gName}


        and g_price = #{gPrice}


        and g_isdiscount = #{gIsdiscount}


        and g_describe = #{gDescribe}


        and g_sales_volume = #{gSalesVolume}


        and g_buy_person_time = #{gBuyPersonTime}


        and g_stock = #{gStock}


        and g_storage_time = #{gStorageTime}


        and g_removal_time = #{gRemovalTime}


        and g_status = #{gStatus}


        and es_1 = #{es1}


        and es_2 = #{es2}


        and es_3 = #{es3}
