mapper.select:
select
gp_id, gp_name, g_id, g_level, es_1, es_2, es_3
from goodsphoto


        and gp_id = #{gpId}


        and gp_name = #{gpName}


        and g_id = #{gId}


        and g_level = #{gLevel}


        and es_1 = #{es1}


        and es_2 = #{es2}


        and es_3 = #{es3}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from goodsphoto


        and gp_id = #{gpId}


        and gp_name = #{gpName}


        and g_id = #{gId}


        and g_level = #{gLevel}


        and es_1 = #{es1}


        and es_2 = #{es2}


        and es_3 = #{es3}
