mapper.select:
select
sc_id, u_id, g_id, sc_weight, sc_addtime, es_1, es_2, es_3
from shoppingcart


        and sc_id = #{scId}


        and u_id = #{uId}


        and g_id = #{gId}


        and sc_weight = #{scWeight}


        and sc_addtime = #{scAddtime}


        and es_1 = #{es1}


        and es_2 = #{es2}


        and es_3 = #{es3}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from shoppingcart


        and sc_id = #{scId}


        and u_id = #{uId}


        and g_id = #{gId}


        and sc_weight = #{scWeight}


        and sc_addtime = #{scAddtime}


        and es_1 = #{es1}


        and es_2 = #{es2}


        and es_3 = #{es3}

mapper.update:
update shoppingcart
set sc_weight=sc_weight + 1
where sc_id = #{scId}

update shoppingcart
set sc_weight=sc_weight - 1
where sc_id = #{scId}
