mapper.select:
select
od_id, o_id, g_id, od_price, od_weight, es_1, es_2, es_3
from orderdetails


        and od_id = #{odId}


        and o_id = #{oId}


        and g_id = #{gId}


        and od_price = #{odPrice}


        and od_weight = #{odWeight}


        and es_1 = #{es1}


        and es_2 = #{es2}


        and es_3 = #{es3}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from orderdetails


        and od_id = #{odId}


        and o_id = #{oId}


        and g_id = #{gId}


        and od_price = #{odPrice}


        and od_weight = #{odWeight}


        and es_1 = #{es1}


        and es_2 = #{es2}


        and es_3 = #{es3}
