mapper.select:
select
u_id, username, passwd, u_email, u_tel, u_sex, u_datetime, u_photo, u_role, u_status, es_1, es_2, es_3
from users


        and u_id = #{uId}


        and username = #{username}


        and passwd = #{passwd}


        and u_email = #{uEmail}


        and u_tel = #{uTel}


        and u_sex = #{uSex}


        and u_datetime = #{uDatetime}


        and u_photo = #{uPhoto}


        and u_role = #{uRole}


        and u_status = #{uStatus}


        and es_1 = #{es1}


        and es_2 = #{es2}


        and es_3 = #{es3}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from users


        and u_id = #{uId}


        and username = #{username}


        and passwd = #{passwd}


        and u_email = #{uEmail}


        and u_tel = #{uTel}


        and u_sex = #{uSex}


        and u_datetime = #{uDatetime}


        and u_photo = #{uPhoto}


        and u_role = #{uRole}


        and u_status = #{uStatus}


        and es_1 = #{es1}


        and es_2 = #{es2}


        and es_3 = #{es3}
