mapper.select:
select
o_id, u_id, o_time, o_status, o_total_price, o_remark, ad_id, o_pay_type, o_freight_price, o_idEnd,
o_pay_status, es_1, es_2, es_3
from orders


        and o_id = #{oId}


        and u_id = #{uId}


        and o_time = #{oTime}


        and o_status = #{oStatus}


        and o_total_price = #{oTotalPrice}


        and o_remark = #{oRemark}


        and ad_id = #{adId}


        and o_pay_type = #{oPayType}


        and o_freight_price = #{oFreightPrice}


        and o_idEnd = #{oIdend}


        and o_pay_status = #{oPayStatus}


        and es_1 = #{es1}


        and es_2 = #{es2}


        and es_3 = #{es3}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from orders


        and o_id = #{oId}


        and u_id = #{uId}


        and o_time = #{oTime}


        and o_status = #{oStatus}


        and o_total_price = #{oTotalPrice}


        and o_remark = #{oRemark}


        and ad_id = #{adId}


        and o_pay_type = #{oPayType}


        and o_freight_price = #{oFreightPrice}


        and o_idEnd = #{oIdend}


        and o_pay_status = #{oPayStatus}


        and es_1 = #{es1}


        and es_2 = #{es2}


        and es_3 = #{es3}
